INSERT INTO "bars" VALUES(4,'Eminem, Danny Brown & More Rep Motor City w/ “Detroit vs. Everybody”','<p><a href="http://okp-cdn.okayplayer.com/wp-content/uploads/2014/11/eminem-shadyxv-detroit-vs-everybody-lead.jpg"><img src="http://okp-cdn.okayplayer.com/wp-content/uploads/2014/11/eminem-shadyxv-detroit-vs-everybody-lead.jpg" alt="Eminem Continues To Hit Hard Ahead Of The Release Of The Forthcoming &#039;SHADYXV&#039; LP With The Motor City Posse Cut &quot;Detroit Vs. Everybody&quot; Featuring Danny Brown, Dej Loaf, Big Sean &amp; More." width="715" height="400" class="alignnone size-full wp-image-81107" /></a></p>
